import React, { useState, useEffect , useContext} from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Copyright from "../../components/Copyright";
import { useNavigate } from "react-router-dom";
import { FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import 'react-toastify/dist/ReactToastify.css';
import { toast } from 'react-toastify';
import { ToastContainer } from 'react-toastify';
import Count from "../../context/Count";

const CustomBox = styled(Box)({
  padding: 10,
});

const CustomBox_For_Form = styled(Box)({
  paddingTop: 50,
  paddingBottom: 50,
  paddingLeft: 100,
  paddingRight: 100,
});

const getToken = () => {
  // Retrieve the token from localStorage or context
  return localStorage.getItem("authToken");
};

function CreateProperties() {
  const [propertyName, setPropertyName] = useState("");
  const [propertyAddress, setPropertyAddress] = useState("");
  const [role, setRole] = useState("");
  const [selectedValue, setSelectedValue] = useState("");
  const [title, setTitle] = useContext(Count);
  const navigate = useNavigate();
  const[alluser , setAlluser] = useState();
  const [Landlord,  setlandlord] = useState();

  useEffect(() => { 
    setTitle("Create Property");
   
  }, [setTitle]);

  useEffect(() => {
    user();
  },[]);

  console.log(alluser)

  const handleChange = (event) => {
   // user();
    // setSelectedValue(event.target.value);
    // setRole(event.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!role) {
      toast.error("Please select a role.");
      return;
    }
    const property = {
      name: propertyName,
      address: propertyAddress,
      role_id: role,
    };

    try {
      const token = getToken();
      const response = await fetch("http://localhost:7000/properties", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(property),
      });
      if (response.ok) {
        toast.success('Property created successfully!');
        setTimeout(() => { navigate("/properties"); }, 2000);
        setPropertyName('');
        setPropertyAddress('');
        setRole('');
        setSelectedValue('');
      } else {
        const errorData = await response.json();
        toast.error(`Error: ${errorData.message}`);
        throw new Error(errorData.message);
      }
    } catch (error) {
      console.error("Error creating property:", error);
    }
  };

  const user = async () => {
    try {
      const token = getToken();
      const response = await fetch("http://localhost:7000/users", {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the header
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch users");
      }

      const data = await response.json();
      
    setAlluser(data.users[0].role_id);
    } catch (error) {
      console.error("Error fetching users:", error);
      toast.error("Error fetching users: " + error.message);
    }
  };
 
 

  return (
    <div>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <CustomBox>
          <Paper>
            <ToastContainer />
            <CustomBox_For_Form>
              <form onSubmit={handleSubmit}>
                <TextField
                  label="Property Name"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  value={propertyName}
                  onChange={(e) => setPropertyName(e.target.value)}
                />

                <TextField
                  label="Property Address"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                  value={propertyAddress}
                  onChange={(e) => setPropertyAddress(e.target.value)}
                />

                <FormControl fullWidth margin="normal">
                  <InputLabel  id="dropdown-label">Landlord</InputLabel>
                  <Select
                    labelId="dropdown-label"
                    id="dropdown"
                    value={selectedValue}
                    label="Select Role"
                    onChange={handleChange}
                  >
                    <MenuItem value="66ab2e0be525f1294827dbcd">Landlord</MenuItem>
                  
                  </Select>
                </FormControl>

                <FormControl fullWidth margin="normal">
                  <InputLabel  id="dropdown-label">Tenant</InputLabel>
                  <Select
                    labelId="dropdown-label"
                    id="dropdown"
                    value={selectedValue}
                    label="Select Role"
                    onChange={handleChange}
                  >
                    <MenuItem value="66ab2e0be525f1294827dbcd">Tenant</MenuItem>
                  
                  </Select>
                </FormControl>


                <br />
                <br />

                <Button variant="contained" color="primary" type="submit">
                  Create Property
                </Button>
              </form>
            </CustomBox_For_Form>
          </Paper>
        </CustomBox>

        <Copyright />
      </Container>
    </div>
  );
}

export default CreateProperties;
