import React, { useEffect } from 'react';
import { useAuth } from '../../context/useAuth';
import { Outlet,Navigate } from 'react-router-dom';

function Authlayout() {
  const { user } = useAuth();

  return (
    <>
    {
        user
          ?
          <Outlet />
          :
          <Navigate to="/login" />
      }
      </>
  )
}

export default Authlayout;
