import React, { createContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import jwtDecode from 'jwt-decode';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const checkAuthToken = async () => {
    const authToken = localStorage.getItem('authToken');
    if (authToken) {
      try {
        const decodedToken = jwtDecode(authToken);
        const currentTime = Date.now() / 1000;

        if (decodedToken.exp < currentTime) {
          logout(); // Token has expired, logout the user
          return;
        }

        const response = await fetch('http://localhost:7000/users/auth/me', {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          if (data.user) {
            setUser(data.user);
          } else {
            logout(); // Invalid token, logout the user
          }
        } else {
          throw new Error('Token validation failed');
        }
      } catch (error) {
        console.error('Error during token validation', error);
        logout();
      }
    }
  };

  useEffect(() => {
    // Initial check
    checkAuthToken();

    // Set interval to check token every 30 seconds
    const intervalId = setInterval(checkAuthToken, 30000);

    // Cleanup interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  const login = (userData) => {
    setUser(userData.user);
    localStorage.setItem('authToken', userData.token); // Simulate storing a token
    navigate('/');
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
