import TableContainer from "@mui/material/TableContainer";
import React, { useContext, useState, useEffect } from "react";
import { styled, createTheme, ThemeProvider } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Container from "@mui/material/Container";
import Paper from "@mui/material/Paper";
import Navbar from "../../components/Navbar";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import Copyright from "../../components/Copyright";
import { useNavigate } from "react-router-dom";
import Count from "../../context/Count";

const defaultTheme = createTheme();

function CreateUsers() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("");
  const navigate = useNavigate();
  const [title, setTitle] = useContext(Count);
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    password: "",
    role: "",
  });

  async function handlesubmit(e) {
    e.preventDefault();
    const user = {
      name,
      email,
      password,
      role_id: role,
    };

    try {
      const response = await fetch("http://localhost:7000/users", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(user),
      });
      if (response.ok) {
        const data = await response.json();
        console.log("User created:", data);
        navigate("/users"); // Redirect to the desired path
      } else {
        console.error("Failed to create user:", await response.text());
      }

      console.log("User created:", response);
      // Handle success, e.g., reset form or show a success message
    } catch (error) {
      console.error("Error creating user:", error);
      // Handle error, e.g., show an error message
    }
  }

  const CustomBox = styled(Box)({
    padding: 10,
  });
  const CustomBox_For_Form = styled(Box)({
    paddingTop: 50,
    paddingBottom: 50,
    paddingLeft: 100,
    paddingRight: 100,
  });
  useEffect(() => {
    setTitle("Create User");
  }, []);

  const onChangeData = (name, value) => {
    setUserData({
      ...userData,
      [name]: value,
    });
  };

  return (
    <div>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <CustomBox>
          <TableContainer component={Paper}>
            <div>
              <CustomBox_For_Form>
                <form onSubmit={handlesubmit}>
                  <TextField
                    label="Name"
                    name="name"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    // value={userData?.name}
                    onChange={(e) => onChangeData("name", e.target.value)}
                  />

                  <TextField
                    label="Email"
                    name="email"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={userData?.email}
                    onChange={(e) => onChangeData("email", e.target.value)}
                  />
                  <TextField
                    label="Password"
                    name="password"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                    value={userData?.password}
                    onChange={(e) => onChangeData("password", e.target.value)}
                  />
                  <TextField
                    label="Role_id"
                    variant="outlined"
                    name="role"
                    fullWidth
                    margin="normal"
                    value={userData?.role}
                    onChange={(e) => onChangeData("role", e.target.value)}
                  />
                  <Button variant="contained" color="primary" type="submit">
                    Create User
                  </Button>
                </form>
              </CustomBox_For_Form>
            </div>
          </TableContainer>
        </CustomBox>

        <Copyright />
      </Container>
    </div>
  );
}

export default CreateUsers;
