// import 'react-toastify/dist/ReactToastify.css';

function Users() {
  const [alluser, setAlluser] = useState([]);
  const [title, setTitle] = useContext(Count);

  const defaultTheme = createTheme();

  const getToken = () => {
    // Retrieve the token from localStorage or context
    return localStorage.getItem("token");
  };

  const fetchUsers = async () => {
    try {
      const token = getToken();
      const response = await fetch("http://localhost:7000/users", {
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the header
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }

      const data = await response.json();
      setAlluser(data.users || []);
    } catch (error) {
      console.error("Error fetching users:", error);
      toast.error("Error fetching users: " + error.message);
    }
  };

  useEffect(() => {
    setTitle("User Management");
    fetchUsers();
  }, [setTitle]);

  const handleDelete = async (user_id) => {
    try {
      const token = getToken();
      const response = await fetch(`http://localhost:7000/users/${user_id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`, // Include the token in the header
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Failed to delete user');
      }

      setAlluser(prevUsers => prevUsers.filter(user => user._id !== user_id));
      toast.success("User deleted successfully!");
    } catch (error) {
      console.error("Error deleting user:", error.message);
      toast.error("Error deleting user: " + error.message);
    }
  };

  return (
    <div>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
        <Link to="/createuser" style={{ textDecoration: "none" }}>
          <Box display="flex" justifyContent="flex-end" mb={2}>
            <Button variant="contained" color="primary">Create User</Button>
          </Box>
        </Link>
        <TableContainer component={Paper}>
          <ToastContainer />
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>Name</TableCell>
                <TableCell align="right">Email</TableCell>
                <TableCell align="right">Role</TableCell>
                <TableCell align="right">Delete User</TableCell>
                <TableCell align="right">Edit User</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {alluser.map((row) => (
                <TableRow
                  key={row._id}
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell component="th" scope="row">{row.name}</TableCell>
                  <TableCell align="right">{row.email}</TableCell>
                  <TableCell align="right">{row?.role_id?.name || 'Unknown'}</TableCell>
                  <TableCell align="right">
                    <Button
                      onClick={() => handleDelete(row._id)}
                      variant="contained"
                      color="primary"
                    >
                      Delete
                    </Button>
                  </TableCell>
                  <TableCell align="right">
                    <Link to={`/users/edit/${row._id}`} style={{ textDecoration: "none" }}>
                      <Button variant="contained" color="primary">Edit</Button>
                    </Link>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        <Copyright />
      </Container>
    </div>
  );
}

export default Users;
